def main():
    import sys
    data = sys.stdin.read().splitlines()
    n, k = map(int, data[0].split())
    s = data[1]

    max_strawberries = 0

    # Check all possible starting positions
    for start in range(n - k + 1):
        healthy_count = 0
        found = False

        # Count healthy teeth until we reach K or end
        for i in range(start, start + k):
            if s[i] == 'O':
                healthy_count += 1

        # If we have at least K healthy teeth, check further
        if healthy_count >= k:
            found = True

            # Continue checking forward
            for j in range(start + k, n):
                if s[j] == 'O':
                    healthy_count += 1
                else:
                    healthy_count = 0

                if healthy_count >= k:
                    found = True
                    break

            if found:
                max_strawberries = max(max_strawberries, (k // 2) * (k + 1))

    print(max_strawberries)

if __name__ == "__main__":
    main()

def main():
    s = input().strip()

    # Check if length is even
    if len(s) % 2 != 0:
        print("No")
        return

    half_length = len(s) // 2

    # Count frequency of each character
    char_count = {}
    for char in s:
        char_count[char] = char_count.get(char, 0) + 1

    # Check if all characters appear exactly twice
    for count in char_count.values():
        if count != 2:
            print("No")
            return

    # Check consecutive pairs
    valid = True
    for i in range(half_length):
        if s[i] != s[i + half_length]:
            valid = False
            break

    print("Yes" if valid else "No")

if __name__ == "__main__":
    main()

