def main():
    A, B, C = map(int, input().split())
    total = A + B + C

    if total % 3 != 0:
        print("No")
        return

    target_sum = total // 3

    # Check all combinations of a, b, c where a <= b <= c
    for a in range(0, min(A, target_sum) + 1):
        for b in range(a, min(B, target_sum - a) + 1):
            c = total - a - b
            if c >= b and c <= B:
                print("Yes")
                return

    print("No")

