def main():
    import sys
    data = sys.stdin.read().splitlines()

    H, W, X, Y = map(int, data[0].split())
    grid = []
    houses = set()

    for i in range(1, 1 + H):
        grid.append(data[i])
        for j in range(1, 1 + W):
            if grid[i-1][j-1] == '@':
                houses.add((i, j))

    T = data[-1]

    x, y = X, Y

    visited = set()
    visited.add((x, y))

    for char in T:
        new_x, new_y = x, y

        if char == 'U' and x > 1 and grid[x-2][y-1] != '#':
            new_x -= 1

        elif char == 'D' and x < H - 1 and grid[x][y-1] != '#':
            new_x += 1

        elif char == 'L' and y > 1 and grid[x-1][y-2] != '#':
            new_y -= 1

        elif char == 'R' and y < W - 1 and grid[x-1][y] != '#':
            new_y += 1

        if (new_x, new_y) not in visited:
            visited.add((new_x, new_y))

        x, y = new_x, new_y

    print(f"{x} {y} {len(houses)}")

if __name__ == "__main__":
    main()

