def main():
    s = input().strip()

    # Check if length is even
    if len(s) % 2 != 0:
        print("No")
        return

    n = len(s)
    half_n = n // 2

    # Count frequency of each character
    freq = {}
    for char in s:
        freq[char] = freq.get(char, 0) + 1

    # Check if all characters appear exactly twice
    for count in freq.values():
        if count != 2:
            print("No")
            return

    # Check if all pairs have matching characters
    for i in range(half_n):
        char1 = s[i]
        char2 = s[half_n + i]

        if char1 != char2:
            print("No")
            return

    print("Yes")

