    import sys
    data = sys.stdin.read().splitlines()
    n, d = map(int, data[0].split())
    s = data[1].strip()

    result = []
    for i in range(n):
        if s[i] == '@':
            result.append('@')
        else:
            # Find last occurrence of @ on or before position i
            j = min(i, d)
            while j >= 0 and s[j] != '@':
                j -= 1
            if j < 0:
                result.append('.')
            else:
                result.append(s[j])

    print(''.join(result))

