import sys

def main():
    data = sys.stdin.read().splitlines()
    H, W, X, Y = map(int, data[0].split())
    grid = []
    for i in range(1, 1 + H):
        grid.append(data[i].strip())

    T = data[-1]

    current_x, current_y = X - 1, Y - 1

    visited_houses = set()
    houses_passed = 0

    for char in T:
        new_x, new_y = current_x, current_y

        if char == 'U':
            new_y -= 1
        elif char == 'D':
            new_y += 1
        elif char == 'L':
            new_x -= 1
        else:  # char == 'R'
            new_x += 1

        if 0 <= new_x < H and 0 <= new_y < W:
            if grid[new_x][new_y] == '#':
                continue

            if grid[new_x][new_y] == '@':
                houses_passed += 1
                visited_houses.add((new_x, new_y))

            current_x, current_y = new_x, new_y

    print(f"{current_x + 1} {current_y + 1} {houses_passed}")

