def main():
    s = input().strip()

    # Precompute all prefixes that have a digit after them
    prefix_map = {}
    for i in range(len(s)):
        if s[i].isdigit():
            prefix_map[s[:i+1]] = True

    # BFS: start with empty string, keep track of current prefix and count
    queue = [(s, 0)]
    visited = set()
    visited.add(s)

    while queue:
        curr_prefix, count = queue.pop(0)

        # Check if we've reached the target prefix
        if curr_prefix == '9' * len(s):
            print(count)
            return

        # Try appending each digit to the current prefix
        for d in '0123456789':
            new_prefix = curr_prefix + d
            if new_prefix in prefix_map and new_prefix not in visited:
                visited.add(new_prefix)
                queue.append((new_prefix, count + 1))

if __name__ == '__main__':
    main()
